function jkInterestingElements(){return $("article");}
var jkKeys={NEXT:'j',PREV:'k'};

var jkCodes={NEXT:jkKeys.NEXT.charCodeAt(),PREV:jkKeys.PREV.charCodeAt()};function jkExecute(event){function closest(){items=jkInterestingElements().filter(":visible");prev=$('html, body');next=null;items.each(function(index,item){itemdistance=$(item).offset().top-$(window).scrollTop();if(itemdistance<0&&(prev==null||itemdistance>$(prev).offset().top-$(window).scrollTop()))
{prev=item;}
if(itemdistance>0&&(next==null||itemdistance<$(next).offset().top-$(window).scrollTop()))
{next=item;}});return{next:next,prev:prev};}
if(!event.which){return;}
if($(event.target).is(':input')){return;}
if(event.which==jkCodes.NEXT&&closest().next!=null)
{$('html, body').scrollTop($(closest().next).offset().top);}
if(event.which==jkCodes.PREV&&closest().prev!=null){$('html, body').scrollTop($(closest().prev).offset().top);}}
$(document).ready(function(){$(document).bind('keypress',jkExecute);});